INSERT INTO `content_info` VALUES ('1', '接口调用方法', '<p><span style=\"font-size: 14px;\"><strong>1.概述</strong></span></p><p><span style=\"font-size: 14px;\">a.服务器提供基于HTTP的功能接口。</span></p><p><span style=\"font-size: 14px;\">b.客户端需调用一个功能接口时，发起一次HTTP请求，方法为POST，附带相应参数。</span></p><p><span style=\"font-size: 14px;\">c.服务器返回JSON字符串作为请求结果，JSON字符串采用UTF-8格式编码。</span></p><p><span style=\"font-size: 14px;\"><strong>2.调用入口</strong></span></p><p><span style=\"font-size: 14px;\">测试环境：http://testapi.miyabaobei.com/class/method/ 正式环境：http://api.miyabaobei.com/class/method/</span></p><p><span style=\"font-size: 14px;\"><strong>3.调用参数</strong></span></p><p><span style=\"font-size: 14px;\">所有参数以POST方式提交，编码为UTF8。</span></p><p><br/></p><table class=\"\\&quot;wiki\\&quot;\"><tbody><tr style=\"\\&quot;font-stretch:\" class=\"\\&quot;firstRow\\&quot;\"><th style=\"\\&quot;font-weight:\"><span style=\"font-size: 14px;\">名称</span></th><th style=\"\\&quot;font-weight:\"><span style=\"font-size: 14px;\">是否必须</span></th><th style=\"\\&quot;font-weight:\"><span style=\"font-size: 14px;\">描述</span></th></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">timestamp</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">整型时间戳，与服务器时间误差10分钟以内</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">app_id</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">分配的ID号</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">version</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">应用版本，格式为操作系统_版本号。例如：iph