INSERT INTO `content_info` VALUES ('7', 'plus接口调用方法', '<p><span style=\"font-size: 14px;\"><strong>1.概述</strong></span></p><p>&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"font-size: 14px;\">a.服务器提供基于HTTP的功能接口。</span></p><p>&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"font-size: 14px;\">b.客户端需调用一个功能接口时，发起一次HTTP请求，方法为POST，附带相应参数。</span></p><p>&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"font-size: 14px;\">c.服务器返回JSON字符串作为请求结果，JSON字符串采用UTF-8格式编码。</span></p><p><span style=\"font-size: 14px;\"><strong>2.调用入口</strong></span></p><p><span style=\"font-size: 14px;\">&nbsp;&nbsp;&nbsp;&nbsp;域名：http://plus.mia.com/class/method</span></p><p><span style=\"font-size: 14px;\">&nbsp;&nbsp;&nbsp;&nbsp;测试环境host：172.16.96.94</span></p><p><strong style=\"font-size: 14px;\">3.调用参数</strong></p><table class=\"\\&quot;wiki\\&quot;\"><tbody><tr style=\"\\&quot;font-stretch:\" class=\"\\&quot;firstRow\\&quot;\"><th style=\"\\&quot;font-weight:\"><span style=\"font-size: 14px;\">名称</span>\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</th><th style=\"\\&quot;font-weight:\"><span style=\"font-size: 14px;\">是否必须</span>\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</th><th style=\"\\&quot;font-weight:\"><span style=\"font-size: 14px;\">描述</span>\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</th></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"word-break: break-all;\"><span style=\"font-size: 14px;\">timestamp</span>\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span>\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">整型时间戳，与服务器时间误差10分钟以内</span>\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"word-break: break-all;\"><span style=\"font-size: 14px;\">client_type</span>\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span>\n &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &n