INSERT INTO `content_info` VALUES (1, '接口调用方法', '<p><span style=\"font-size: 14px;\"><strong>1.概述</strong></span></p><p><span style=\"font-size: 14px;\">a.服务器提供基于HTTP的功能接口。</span></p><p><span style=\"font-size: 14px;\">b.客户端需调用一个功能接口时，发起一次HTTP请求，方法为POST，附带相应参数。</span></p><p><span style=\"font-size: 14px;\">c.服务器返回JSON字符串作为请求结果，JSON字符串采用UTF-8格式编码。</span></p><p><span style=\"font-size: 14px;\"><strong>2.调用入口</strong></span></p><p><span style=\"font-size: 14px;\">测试环境：http://testapi.miyabaobei.com/class/method/ 正式环境：http://api.miyabaobei.com/class/method/</span></p><p><span style=\"font-size: 14px;\"><strong>3.调用参数</strong></span></p><p><span style=\"font-size: 14px;\">所有参数以POST方式提交，编码为UTF8。</span></p><p><br/></p><table class=\"\\&quot;wiki\\&quot;\"><tbody><tr style=\"\\&quot;font-stretch:\" class=\"\\&quot;firstRow\\&quot;\"><th style=\"\\&quot;font-weight:\"><span style=\"font-size: 14px;\">名称</span></th><th style=\"\\&quot;font-weight:\"><span style=\"font-size: 14px;\">是否必须</span></th><th style=\"\\&quot;font-weight:\"><span style=\"font-size: 14px;\">描述</span></th></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">timestamp</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">整型时间戳，与服务器时间误差10分钟以内</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">app_id</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">分配的ID号</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">version</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">应用版本，格式为操作系统_版本号。例如：iphone_1_0_1</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">sign</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">签名</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">session</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">用户唯一ID，使用手机UUID生成，确保唯一</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">auth_session</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">用户登陆Session,调用登陆接口时返回</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">device_token</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">N</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">当前app终端唯一识别,android传友盟提供的token，获取不到时传空</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">channel_code</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">N</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">android 推广渠道号 ios忽略(V3.0开始android和ios都可使用channel_code)</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">ios_idfa</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">N</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">ios 客户端idfa</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">ios_openudid</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">N</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">ios 客户端openudid</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">ios_uuid</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">N</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">ios 客户端 uuid</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">ios_mac</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">N</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">ios 客户端 mac地址</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">android_mac</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">N</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">android 客户端 mac地址(V3.0)</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">v3.9废弃 mia_channel</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">N</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">app被外部应用调用时的来源渠道(since v3.6)(v3.9废弃)</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">v3.9废弃 mia_sub_channel</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">N</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">app被外部应用调用时的来源二级渠道(since v3.6)(v3.9废弃)</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">params</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">具体接口中所指定的业务参数json字符串</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">dvc_id</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">移动端唯一标示,imei+mac+设备名称&nbsp;v3.9新增</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">regid</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">小米发送push标示&nbsp;v4.0新增</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">no_encrypt</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">N</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">params参数是否加密过，0有加密 1没有加密&nbsp;v5.0新增</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">push_provider</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">N</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">push发送平台 1：小米 2：友盟&nbsp;v5.2新增</span></td></tr></tbody></table><p><span style=\"font-size: 14px;\"><strong>4.签名</strong></span></p><p><span style=\"font-size: 14px;\">调用接口时对所有POST数据（除sign字段）进行签名认证 A：将所有字段（除SIGN字段外）按键值字典序排列。</span></p><p><span style=\"font-size: 14px;\">如将foo=1,bar=2,baz=3 排序为bar=2,baz=3,foo=1，参数名和参数值链接后，得到拼装字符串bar2baz3foo1</span></p><p><span style=\"font-size: 14px;\">B：将应用对应密钥附在上一步字符串尾后MD5加密：</span></p><p><span style=\"font-size: 14px;\">格式为：md5(bar2baz3foo1secret)</span></p><p><span style=\"font-size: 14px;\">C：将上一步最终得到的字符串作为sign字段的值</span></p><p><span style=\"font-size: 14px;\"><strong>5.响应信息</strong></span></p><table class=\"\\&quot;wiki\\&quot;\"><tbody><tr style=\"\\&quot;font-stretch:\" class=\"\\&quot;firstRow\\&quot;\"><th style=\"\\&quot;font-weight:\"><span style=\"font-size: 14px;\">名称</span></th><th style=\"\\&quot;font-weight:\"><span style=\"font-size: 14px;\">是否必须</span></th><th style=\"\\&quot;font-weight:\"><span style=\"font-size: 14px;\">数据类型</span></th><th style=\"\\&quot;font-weight:\"><span style=\"font-size: 14px;\">描述</span></th></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">code</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">string</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">错误识别码</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">msg</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">string</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">错误描述</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">alert</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">string</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">错误输出</span></td></tr><tr style=\"\\&quot;font-stretch:\"><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">content</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">Y</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">string</span></td><td style=\"\\&quot;border-color:\"><span style=\"font-size: 14px;\">响应数据</span></td></tr></tbody></table><p><br/></p><p><span style=\"font-size: 14px;\">例:</span></p><p><span style=\"font-size: 14px;\"></span></p><pre class=\"\\&quot;brush:js;toolbar:false;\\&quot;\"><span style=\"font-size: 14px;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&quot;code&quot;:500,&nbsp;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&quot;msg&quot;:&quot;Invalid&nbsp;arguments:num_iid&quot;,<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&quot;content&quot;:<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;{<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}<br/></span></pre><p><span style=\"font-size: 14px;\"><br/></span></p><p><br/></p>', 1, 'wangjue@mia.com', 'wangjue@mia.com', '2017-07-27 15:52:50', '2017-07-27 16:13:35');
